module.exports = (callbackAsync) => {
    return {
        collection: (name) => ({
            find: (q, req) => callbackAsync({
              action: 'documentStore.collection.find',
              requestId: req.requestId,
              data: {
                query: q,
                collection: name
              }
            }),
            findOne: (q, req) => callbackAsync({
              action: 'documentStore.collection.findOne',
              requestId: req.requestId,
              data: {
                query: q,
                collection: name,               
              }
            })
          })
    }
}