const { getCallback } = require('./registryUtils')

module.exports = (registry) => {
  return {
    resolveEntityFromPath: (path, entitySet, req) => getCallback(registry, req)({
      action: 'folders.resolveEntityFromPath',
      requestRootId: req.context.rootId,
      data: {
        path,
        entitySet
      }
    })
  }
}
